INSERT INTO boardtbl (userid, nickname, title, content, genre, tab, regdate, bookid, ip) VALUES
(1, '수진이', '이야기 속에서 배우는 삶의 교훈', '삶의 교훈을 책을 통해 배우는 여정을 다뤄봅니다. 책을 읽으면 더욱 깊이 있는 사람이 되는 것 같습니다.', '문학', '일반', '2024-12-01 12:00:00', 23, '127.0.0.1'),
(2, '지혜', '오늘의 철학적 고민', '철학은 왜 중요한지 고민하는 글입니다. 여러 철학자의 이야기를 통해 생각을 넓혀보세요.', '인문학', '일반', '2024-12-01 12:05:00', 5, '127.0.0.1'),
(3, '정훈이', '긍정적인 마음가짐의 중요성', '긍정적인 마음가짐은 어떠한 상황에서도 우리에게 힘을 줍니다. 책에서 배운 긍정의 힘을 나누고 싶습니다.', '자기계발', '감상', '2024-12-01 12:10:00', 18, '127.0.0.1'),
(1, '수진이', '기억에 남는 역사적 순간', '우리의 역사를 돌아보며 기억에 남는 순간들을 이야기해 봅니다. 역사 속의 중요한 순간들을 되새겨보세요.', '역사', '추천', '2024-12-01 12:15:00', 33, '127.0.0.1'),
(2, '지혜', '경제의 기본 원칙', '경제경영 책을 통해 배우는 경제의 기본 원칙. 기본적인 경제 이론을 잘 이해하면 세상이 조금 더 쉬워 보입니다.', '경제경영', '일반', '2024-12-01 12:20:00', 10, '127.0.0.1'),
(3, '정훈이', '과학적 사고의 중요성', '과학적 사고는 우리가 살아가는 데 필수적입니다. 실험과 데이터를 바탕으로 사고를 전개하는 방식이 중요합니다.', '과학', '일반', '2024-12-01 12:25:00', 44, '127.0.0.1'),
(1, '수진이', '심리학적 접근을 통한 자기 이해', '심리학을 통해 자신을 더 잘 이해하는 법에 대해 이야기해봅니다. 자기 이해는 삶을 풍요롭게 만듭니다.', '인문학', '감상', '2024-12-01 12:30:00', 26, '127.0.0.1'),
(2, '지혜', '만화 속에서 배우는 삶의 진리', '만화도 철학과 삶의 진리를 이야기할 수 있습니다. 만화를 통해 우리는 많은 것을 배울 수 있습니다.', '만화', '추천', '2024-12-01 12:35:00', 12, '127.0.0.1'),
(3, '정훈이', '다시 돌아본 과학의 발전', '과학은 어떻게 발전했는지, 그리고 그것이 오늘날 우리 생활에 미친 영향을 다루어봅니다.', '과학', '일반', '2024-12-01 12:40:00', 7, '127.0.0.1'),
(1, '수진이', '사회과학을 통한 사회적 이슈 탐구', '사회과학을 통해 우리는 사회적 이슈를 어떻게 해결할 수 있을지에 대해 생각해 봅니다.', '사회과학', '일반', '2024-12-01 12:45:00', 29, '127.0.0.1'),
(2, '지혜', '현대인의 정신 건강', '현대 사회에서 정신 건강은 무엇보다 중요합니다. 정신 건강을 돌보는 여러 방법들을 다뤄봅니다.', '인문학', '감상', '2024-12-01 12:50:00', 41, '127.0.0.1'),
(3, '정훈이', '철학이 주는 깊은 통찰력', '철학이 주는 깊은 통찰력은 삶을 더욱 풍성하게 만들어 줍니다. 다양한 철학적 사고 방식을 소개합니다.', '인문학', '일반', '2024-12-01 12:55:00', 33, '127.0.0.1'),
(1, '수진이', '새로운 자기계발서의 발견', '최근에 읽은 자기계발서를 소개합니다. 삶의 변화가 필요할 때마다 자기계발서는 큰 도움이 됩니다.', '자기계발', '추천', '2024-12-01 13:00:00', 2, '127.0.0.1'),
(2, '지혜', '역사를 통해 배우는 교훈', '역사 속에서 중요한 교훈을 얻을 수 있습니다. 우리는 역사를 통해 더 나은 미래를 만들 수 있습니다.', '역사', '일반', '2024-12-01 13:05:00', 22, '127.0.0.1'),
(3, '정훈이', '기술의 발전과 우리의 삶', '기술의 발전은 우리의 삶을 어떻게 변화시켰는지, 그 영향력을 탐구하는 글입니다.', '과학', '일반', '2024-12-01 13:10:00', 50, '127.0.0.1'),
(1, '수진이', '문학 속 인물의 심리 분석', '문학 속 인물들을 통해 그들의 심리를 분석하고, 인간의 본질에 대해 고민해봅니다.', '문학', '감상', '2024-12-01 13:15:00', 11, '127.0.0.1'),
(2, '지혜', '자기계발을 위한 실천법', '실천이 중요합니다. 자기계발을 위해 어떻게 실천해야 할지에 대한 구체적인 방법을 다루어봅니다.', '자기계발', '일반', '2024-12-01 13:20:00', 19, '127.0.0.1'),
(3, '정훈이', '사회과학과 정치적 이슈', '사회과학을 통해 정치적 이슈를 바라보는 시각에 대해 생각해봅니다. 사회적 변화가 정치에 미치는 영향.', '사회과학', '추천', '2024-12-01 13:25:00', 44, '127.0.0.1'),
(1, '수진이', '인문학과 과학의 만남', '인문학과 과학은 서로 다르지만, 그 만남에서 많은 교훈을 얻을 수 있습니다.', '인문학', '일반', '2024-12-01 13:30:00', 8, '127.0.0.1'),
(2, '지혜', '감성적 글쓰기의 중요성', '감성적 글쓰기는 인간의 감정을 표현하는 중요한 수단입니다. 어떻게 글쓰기를 통해 감성을 표현할 수 있을까요?', '문학', '감상', '2024-12-01 13:35:00', 37, '127.0.0.1'),
(3, '정훈이', '경제적 사고를 기르는 방법', '경제적 사고는 현실에서 중요한 도구입니다. 어떻게 경제적 사고를 기를 수 있을까요?', '경제경영', '일반', '2024-12-01 13:40:00', 55, '127.0.0.1'),
(1, '수진이', '삶에서의 작고 큰 변화', '작은 변화가 큰 변화를 불러올 수 있습니다. 변화가 일어나는 과정과 그 중요성에 대해 이야기해봅니다.', '자기계발', '일반', '2024-12-01 13:45:00', 42, '127.0.0.1'),
(2, '지혜', '책이 주는 힘', '책을 읽고 느낀 점들을 정리하며, 책이 우리에게 주는 힘과 깨달음을 나눠봅니다.', '문학', '추천', '2024-12-01 13:50:00', 61, '127.0.0.1'),
(3, '정훈이', '과학과 기술의 미래', '미래의 과학과 기술은 우리에게 어떤 변화를 가져올까요? 그 가능성에 대해 다뤄봅니다.', '과학', '일반', '2024-12-01 13:55:00', 9, '127.0.0.1'),
(1, '수진이', '과거와 현재의 경제 비교', '과거와 현재의 경제 상황을 비교하며, 경제가 어떻게 발전했는지 살펴봅니다.', '경제경영', '일반', '2024-12-01 14:00:00', 16, '127.0.0.1'),
(2, '지혜', '정치적 이슈와 사회적 반응', '정치적 이슈가 사회에 미치는 영향을 탐구하는 글입니다. 사람들이 어떻게 반응하고 있는지 살펴봅니다.', '사회과학', '일반', '2024-12-01 14:05:00', 62, '127.0.0.1'),
(3, '정훈이', '성공을 위한 마음가짐', '성공을 위한 마음가짐과 그 과정을 이야기해봅니다. 마음가짐 하나로 인생이 달라질 수 있습니다.', '자기계발', '추천', '2024-12-01 14:10:00', 30, '127.0.0.1'),
(1, '수진이', '심리학과 자기 이해', '심리학을 통해 자신을 이해하고, 더 나은 사람으로 성장하는 방법에 대해 이야기합니다.', '심리학', '일반', '2024-12-01 14:15:00', 11, '127.0.0.1'),
(2, '지혜', '과학의 발전과 사회 변화', '과학의 발전은 사회에 어떤 영향을 미쳤을까요? 과학과 사회의 관계를 다뤄봅니다.', '과학', '일반', '2024-12-01 14:20:00', 54, '127.0.0.1'),
(3, '정훈이', '현대 경제의 문제점', '현대 경제에서 우리가 직면한 문제점들을 탐구하고, 그 해결책을 찾아봅니다.', '경제경영', '일반', '2024-12-01 14:25:00', 49, '127.0.0.1'),
(1, '수진이', '자기계발을 위한 책 추천', '자기계발을 위해 읽어야 할 책들을 추천하며, 그 내용에 대해 간략히 소개합니다.', '자기계발', '추천', '2024-12-01 14:30:00', 77, '127.0.0.1'),
(2, '지혜', '철학적 사고의 기초', '철학적 사고를 기르기 위한 기초적인 방법들을 소개합니다. 철학은 삶에 깊이를 더합니다.', '인문학', '일반', '2024-12-01 14:35:00', 34, '127.0.0.1'),
(3, '정훈이', '영화 속 과학', '영화 속 과학적 요소들을 분석하고, 그것이 실제 과학과 어떤 차이가 있는지 비교해봅니다.', '과학', '감상', '2024-12-01 14:40:00', 14, '127.0.0.1'),
(1, '수진이', '인문학적 관점에서 본 사회 문제', '사회 문제를 인문학적인 관점에서 바라보며, 해결책을 모색해봅니다.', '인문학', '일반', '2024-12-01 14:45:00', 43, '127.0.0.1'),
(2, '지혜', '현대사회의 철학적 고민', '현대사회의 문제에 대해 철학적 고민을 다루며, 우리가 마주한 이슈들에 대해 생각해봅니다.', '철학', '일반', '2024-12-01 14:50:00', 25, '127.0.0.1'),
(3, '정훈이', '심리학적 분석을 통한 자기 발전', '심리학적 분석을 통해 자신을 발전시킬 수 있는 방법들을 다뤄봅니다.', '심리학', '감상', '2024-12-01 14:55:00', 13, '127.0.0.1'),
(1, '수진이', '기술 발전의 역사', '기술 발전이 어떻게 이루어졌는지, 그 역사를 따라가며 배워봅니다.', '과학', '추천', '2024-12-01 15:00:00', 58, '127.0.0.1'),
(2, '지혜', '경제학의 기초 원리', '경제학의 기본 원리들을 쉽게 설명하며, 어떻게 실생활에 적용할 수 있을지 이야기합니다.', '경제경영', '일반', '2024-12-01 15:05:00', 31, '127.0.0.1'),
(3, '정훈이', '문학과 현대 사회', '문학 작품을 통해 현대 사회를 어떻게 바라볼 수 있는지 그 관계를 다루어봅니다.', '문학', '일반', '2024-12-01 15:10:00', 70, '127.0.0.1'),
(1, '수진이', '책을 통해 배우는 경제', '책을 통해 경제를 배운 경험을 나누고, 어떻게 경제적 사고를 할 수 있는지 알려드립니다.', '경제경영', '추천', '2024-12-01 15:15:00', 20, '127.0.0.1');
INSERT INTO boardtbl (userid, nickname, title, content, genre, tab, regdate, bookid, ip) VALUES
(1, '수진이', '책 속의 또 다른 세계', '책을 읽으면 새로운 세상이 열린다. 나의 인생을 바꿀 수 있는 책들을 소개한다.', '문학', '추천', '2024-12-01 10:00:00', 1, '127.0.0.1'),
(2, '지혜', '나를 성장시키는 책들', '자기계발서를 통해 내 삶을 바꾸고 싶어. 여러분에게 추천하고 싶은 책들이 많아요.', '자기계발', '추천', '2024-12-01 11:00:00', 2, '127.0.0.1'),
(3, '정훈이', '경제적 자유를 꿈꾸며', '경제경영 분야의 책을 읽고 나서 내 미래가 어떻게 달라졌는지 이야기해볼게요.', '경제경영', '감상', '2024-12-01 12:00:00', 3, '127.0.0.1'),
(1, '수진이', '시작이 반이다', '어떤 일을 시작할 때, 가장 중요한 건 첫걸음이란 사실을 다시금 느꼈어요.', '자기계발', '일반', '2024-12-02 10:15:00', 4, '127.0.0.1'),
(2, '지혜', '과학의 기초를 배우는 법', '과학에 대한 기본적인 이해부터 시작하는 방법을 나누고 싶습니다.', '과학', '일반', '2024-12-02 11:15:00', 5, '127.0.0.1'),
(3, '정훈이', '조용한 리더십', '리더십에 대해 많은 책을 읽었지만, 그 중 가장 기억에 남는 책을 공유하고자 합니다.', '인문학', '감상', '2024-12-02 12:15:00', 6, '127.0.0.1'),
(1, '수진이', '내 인생의 결정적인 순간', '이 책을 읽고 인생에서 중요한 순간들을 어떻게 대처해야 할지 깨달았다.', '문학', '감상', '2024-12-03 10:30:00', 7, '127.0.0.1'),
(2, '지혜', '문학의 힘', '문학을 통해 삶을 어떻게 더 풍성하게 살 수 있는지에 대한 생각을 나눠봅니다.', '문학', '추천', '2024-12-03 11:30:00', 8, '127.0.0.1'),
(3, '정훈이', '사회과학의 이해', '사회과학을 공부하는 방법과 중요성을 고민한 책을 소개합니다.', '사회과학', '일반', '2024-12-03 12:30:00', 9, '127.0.0.1'),
(1, '수진이', '인문학의 매력', '인문학을 통해 더 넓은 세상과 생각을 가질 수 있다는 걸 알려주는 책.', '인문학', '추천', '2024-12-04 10:45:00', 10, '127.0.0.1'),
(2, '지혜', '행복에 관한 철학', '행복이 무엇인지에 대한 철학적 사고를 바탕으로 책을 읽고 느낀 점을 공유합니다.', '인문학', '일반', '2024-12-04 11:45:00', 11, '127.0.0.1'),
(3, '정훈이', '경제적 사고의 변화', '경제경영 분야의 책을 통해 사고를 어떻게 바꾸었는지 이야기하고 싶어요.', '경제경영', '추천', '2024-12-04 12:45:00', 12, '127.0.0.1'),
(1, '수진이', '감성의 힘', '감성지능이 어떻게 사람들과 소통을 돕고 인생을 바꿀 수 있는지 알아보았습니다.', '심리학', '일반', '2024-12-05 10:00:00', 13, '127.0.0.1'),
(2, '지혜', '종교와 철학', '종교와 철학이 만나는 지점에서 내 삶을 바꾼 책을 소개합니다.', '종교', '감상', '2024-12-05 11:00:00', 14, '127.0.0.1'),
(3, '정훈이', '다시, 시작하는 마음', '책을 통해 나는 다시 시작하는 마음을 가지게 되었고, 이 감정을 함께 나누고 싶습니다.', '자기계발', '일반', '2024-12-05 12:00:00', 15, '127.0.0.1'),
(1, '수진이', '역사의 교훈', '역사를 배우면서 우리가 배워야 할 중요한 교훈들에 대해 이야기합니다.', '역사', '추천', '2024-12-06 10:15:00', 16, '127.0.0.1'),
